import React, { useEffect } from "react";
import "./style.css";
import searchIcon from "./search.svg";
import MovieCard from "./MovieCard";
const API_url = "https://www.omdbapi.com/?i=tt3896198&apikey=f4796d8c";

const  movie1 = {
    "Title": "Fighting, Flying and Driving: The Stunts of Spiderman 3",
    "Year": "2007",
    "imdbID": "tt1132238",
    "Type": "movie",
    "Poster": "https://m.media-amazon.com/images/M/MV5BNTI3NDE1ZmEtMTRiMS00YTY4LTk0OGItNjY4YmI0MDM4OGM4XkEyXkFqcGdeQXVyODE2NDgwMzM@._V1_SX300.jpg"
};
const App = () => {
  const searchMovie = async (title) => {
    const response = await fetch(`${API_url}&s=${title}`);
    const data = await response.json();
    console.log(data);
  };
  useEffect(() => {
    searchMovie("Spiderman");
  });
  return (
    <div className="app">
      <h1>Movieland</h1>
      <div className="search">
        <input placeholder="Search for movies" value="superman" 
        onChange={()=> {}}/>
        <img src={searchIcon}  
        alt="Search"
        onClick={()=> {}}></img>
      </div>
      <div className="container">
      <div className="movie">
        <div>
            <p>{movie1.Year}</p>
        </div>
        <div>
            <img src={movie1.Poster!== 'N/A' ? movie1.Poster : 'https://via.placeholder.com/400'} alt={movie1.title}/>
        </div>
        <div>
            <span>{movie1.Type}</span>
            <h3>{movie1.Title}</h3>
        </div>
      </div>

      </div>
    </div>
  );
};
export default App;
