import "./App.css";
import { useState } from "react";
const App = () => {
  const [counter,setCounter] = useState(0);
  return <>
  <button onClick={setCounter((prevCounter)=> prevCounter +1)}>+</button>
  <button>+</button>
  <h1> {counter}</h1>
  <button onClick={setCounter((prevCounter)=> prevCounter -1)}>-</button>
  <button>-</button>
  </>;

};

export default App;
