import "./App.css";
import { useState } from "react";
const App = () => {
  const [counter, setCounter] = useState(0);
  return (
    <div>
      <button onClick={() => setCounter((prevCounter) => prevCounter + 1)}>
        +
      </button>
      <h1> {counter}</h1>
      <button onClick={() => setCounter((prevCounter) => prevCounter - 1)}>
        -
      </button>
    </div>
  );
};

export default App;
