import './App.css';
import { useState } from 'react';
function App() {
  const [Counter,setCounter]= useState(0);
  return <div >
    <button onClick={()=> {setCounter((prevCounter)=>prevCounter *3)}}>+</button>
    <h1>{Counter}</h1>
    <button onClick={()=> {setCounter((prevCounter)=> prevCounter *2)}}>-</button>
  </div>
}

export default App;
